%% Fit: 'exp1fit'.
[xData, yData] = prepareCurveData( X, EELS );

% Set up fittype and options.
ft = fittype( 'exp1' );
opts = fitoptions( ft );
opts.Display = 'Off';
opts.Lower = [-Inf -Inf];
opts.Robust = 'Bisquare';
%opts.StartPoint = [795.757822400637 -1.88367544204257];
